r80 <- rnorm(80)r60 <- rnorm(60, mean=20, sd=10)r <- append(r80, r60)h<-print.stat(r, need.hist=T)# из гистограммы видно, что данные не однородныеfreq <- h$densityplot(c(0, cumsum(freq))~ seq(min(r), max(r), by=(max(r)-min(r))/(length(freq))), type='b',     xlab='x', ylab='F*', main='Гистограмма кумулятивных частот')# на гистограмме кум. частот виден резкий скачок => неоднородность данных# вариационный рядvar.r <- r[order(r)]summary(var.r)